import React from 'react';
import CipherFactory from '../../ui/EncryptDecrypt';

export default function ADFGVX({ ongetInfo }) {

    function generateGrid(key) {
        key = String(key).toUpperCase();
        const alphabet = 'ADFGVX';
        const grid = [];
        let sortedKey = [...new Set(key)].sort((a, b) => key.indexOf(a) - key.indexOf(b)).join('');
        if (sortedKey.length !== 36) {
            const missingChars = alphabet.split('').filter(char => !sortedKey.includes(char)).join('');
            sortedKey += missingChars;
        }
        for (let char of sortedKey) {
            const row = [char];
            for (let letter of alphabet) {
                if (letter === char) continue;
                row.push(alphabet[(alphabet.indexOf(char) + alphabet.indexOf(letter)) % 6]);
            }
            grid.push(row);
        }
        return grid;
    }
    
    // Function to encode plaintext using the ADFGVX cipher
    function encode(message, key) {
        message = message.toUpperCase().replace(/\s/g, '');
        const grid = generateGrid(key);
        let cipher = '';
        for (let char of message) {
            for (let i = 0; i < grid.length; i++) {
                for (let j = 0; j < grid[i].length; j++) {
                    if (grid[i][j] === char) {
                        cipher += 'ADFGVX'[i] + 'ADFGVX'[j];
                        break;
                    }
                }
            }
        }
        return cipher;
    }
    
    // Function to decode ciphertext using the ADFGVX cipher
    function decode(cipher, key) {
        const grid = generateGrid(key);
        let message = '';
        for (let i = 0; i < cipher.length; i += 2) {
            const row = 'ADFGVX'.indexOf(cipher[i]);
            const col = 'ADFGVX'.indexOf(cipher[i + 1]);
            message += grid[row][col];
        }
        return message;
    }

    const showInformation = () => {
        const info = (
            <>
                <p>
                    The ADFGVX cipher is a field cipher used by the German Army during World War I.
                </p>
                <p>
                    To encrypt using the ADFGVX cipher, a Polybius square and a transposition cipher are combined.
                </p>
                <ul>
                    <li>Substitution Cipher: The plaintext is first converted into pairs of ADFGVX characters based on their positions in a Polybius square.</li>
                    <li>Transposition: The resulting pairs are then reordered according to a keyword.</li>
                    <li>Encryption: The final result is the encrypted ciphertext.</li>
                    <li>Decryption: The ciphertext is reordered according to the keyword to retrieve the pairs of ADFGVX characters, which are then converted back into plaintext.</li>
                </ul>
            </>
        );
        ongetInfo(info);
    };

    React.useEffect(() => {
        showInformation();
    }, []);

    return (
        <>
            <CipherFactory encode={encode} decode={decode} keyComponentC={0} />
        </>
    );
};
