import { Row, InputNumber, Input } from 'antd';
import React, { useState } from 'react';
import SmoothTextInput from './SmoothText';

export default function CipherFactory({ encode, decode, keyComponentA, keyComponentB, keyComponentC }) {
    const [leftText, setLeftText] = useState('');
    const [rightText, setRightText] = useState('');
    const [shift1, setShift1] = useState(keyComponentA === "STR" ? '' : 0);
    const [shift2, setShift2] = useState(keyComponentB === "STR" ? "" : 0);
    const [shift3, setShift3] = useState(keyComponentC || '');

    const setKey1 = (userKeyInput) => {
        const text = keyComponentA === 'STR' ? userKeyInput.target.value : userKeyInput;
        keyComponentA === "STR" ? setShift1(text) : setShift1(Number(text));
        setRightText(encode(leftText, text, shift2, shift3));
    }

    const setKey2 = (userKeyInput) => {
        const text = keyComponentB === 'STR' ? userKeyInput.target.value : userKeyInput;
        keyComponentB === "STR" ? setShift2(text) : setShift2(Number(text));
        setRightText(encode(leftText, shift1, text, shift3));
    }

    const setKey3 = (userKeyInput) => {
        const text = userKeyInput.target.value;
        setShift3(text);
        setRightText(encode(leftText, shift1, shift2, text));
    }

    const handleLeftTextChange = (event) => {
        const text = event.target.value;
        setLeftText(text);
        setRightText(encode(text, shift1, shift2, shift3));
    };

    const handleRightTextChange = (event) => {
        const text = event.target.value;
        setRightText(text);
        setLeftText(decode(text, shift1, shift2, shift3));
    };

    return (
        <>
            {keyComponentA &&
                <Row style={{ display: 'flex', padding: '10px 10px 10px 0px' }}>
                    {
                        (keyComponentA === "STR") ? <Input style={{ flex: 0.5 }} addonAfter="Cipher Key" placeholder='SecretKey' value={shift1} size='large' onChange={setKey1} />
                            : <InputNumber style={{ flex: 0.4 }} addonBefore="Cipher Key A:" placeholder="Shift value" value={shift1} size='large' onChange={setKey1} />
                    }
                </Row>
            }
            {keyComponentB &&
                <Row style={{ display: 'flex', padding: '10px 10px 10px 0px' }}>
                    {
                        (keyComponentB === "STR") ? <Input style={{ flex: 0.5 }} addonAfter="Cipher Key B:" placeholder='SecretKey' value={shift2} size='large' onChange={setKey2} />
                            : <InputNumber style={{ flex: 0.4 }} addonBefore="Cipher Key B:" placeholder="Shift value" value={shift2} size='large' onChange={setKey2} />
                    }
                </Row>
            }
            {keyComponentC &&
                <Row style={{ display: 'flex', padding: '10px 10px 10px 0px' }}>
                    <Input style={{ flex: 0.5 }} addonAfter="Cipher Key C:" placeholder='Enter text' value={shift3} size='large' onChange={setKey3} />
                </Row>
            }
            <Row style={{ display: 'flex' }}>
                <SmoothTextInput
                    value={leftText}
                    isLeft={true}
                    onChange={handleLeftTextChange}
                    placeholder="Enter text to Encrypt"
                />
                <SmoothTextInput
                    value={rightText}
                    isLeft={false}
                    onChange={handleRightTextChange}
                    placeholder="Enter text to Decrypt"
                />
            </Row>
        </>
    );
};
