import React from 'react';
import CipherFactory from '../../ui/EncryptDecrypt';


export default function CipherAscii({ongetInfo}) {

    function generateGrid(keyword) {
        let sortedKeyword = keyword.split('').sort().join('');
        let grid = [];
        let index = 0;
    
        for (let i = 0; i < keyword.length; i++) {
            grid.push([]);
            for (let j = 0; j < keyword.length; j++) {
                grid[i].push(sortedKeyword.indexOf(keyword[index]) + 1);
                index++;
            }
        }
        return grid;
    }
    
    // Function to encrypt the plaintext using the AMSCO cipher
    function encode(plaintext, keyword) {
        let grid = generateGrid(keyword);
        let ciphertext = '';
    
        // Iterate through the plaintext
        for (let i = 0; i < plaintext.length; i++) {
            let row = -1;
            let col = -1;
    
            // Find the row and column of the current character in the grid
            for (let j = 0; j < grid.length; j++) {
                if (grid[j].indexOf(i % keyword.length + 1) !== -1) {
                    row = j;
                    col = grid[j].indexOf(i % keyword.length + 1);
                    break;
                }
            }
            // Append the character at the corresponding position in the grid
            ciphertext += plaintext.charAt(row + col * Math.ceil(plaintext.length / keyword.length));
        }
        return ciphertext;
    }
    
    // Function to decrypt the ciphertext using the AMSCO cipher
    function decode(ciphertext, keyword) {
        let grid = generateGrid(keyword);
        let plaintext = '';
    
        // Iterate through the ciphertext
        for (let i = 0; i < ciphertext.length; i++) {
            let row = -1;
            let col = -1;
    
            // Find the row and column of the current character in the grid
            for (let j = 0; j < grid.length; j++) {
                if (grid[j].indexOf(i % keyword.length + 1) !== -1) {
                    row = j;
                    col = grid[j].indexOf(i % keyword.length + 1);
                    break;
                }
            }
            // Append the character at the corresponding position in the grid
            plaintext += ciphertext.charAt(row + col * Math.ceil(ciphertext.length / keyword.length));
        }
        return plaintext;
    }      
  const showInformation = () => {
    const info = (
        <>
            <p>
                The ASCII Cipher is a simple character substitution cipher based on the ASCII encoding standard. 
                Each character in the plaintext is replaced with its corresponding ASCII value.
            </p>
            <p>
                To encrypt using the ASCII Cipher, simply convert each character of your text to its ASCII value.
            </p>
            <ul>
                <li>Substitution Cipher: Each character in the plaintext is replaced by its corresponding ASCII value.</li>
                <li>ASCII Encoding: ASCII (American Standard Code for Information Interchange) is a character encoding standard that represents text in computers and other devices.</li>
                <li>Encryption: To encrypt, convert each character in the plaintext to its ASCII value.</li>
                <li>Decryption: Decryption involves converting each ASCII value back to its corresponding character.</li>
                <li>Example: 'A' becomes '65', 'B' becomes '66', 'C' becomes '67', and so forth.</li>
            </ul>
        </>
    );
    ongetInfo(info);
};

React.useEffect(() => {
    showInformation();
}, []);

      return <CipherFactory encode={encode} decode={decode} keyComponentA={'abc'}/>
};
