import React from 'react';
import CipherFactory from '../../ui/EncryptDecrypt';

export default function BifidCipher({ ongetInfo }) {
    // Function to encrypt plaintext using the Autokey cipher

    function buildPolybiusSquare(key) {
        // Remove duplicates and keep only alphabets (A-Z)
        if (!key) {
            throw new Error("Error: Please provide a key for encryption.");
          }
        const alphabet = [...new Set(key.toUpperCase().replace(/[^A-Z]/g, ''))];
      
        // Create a 5x5 square (adjust size if needed)
        const squareSize = 5;
        const square = [];
        for (let i = 0; i < squareSize; i++) {
          square.push(alphabet.slice(i * squareSize, (i + 1) * squareSize));
        }
        return square;
      }
      
      function findLetterIndex(letter, square) {
        
        for (let row = 0; row < square.length; row++) {
          const index = square[row].indexOf(letter);
          if (index !== -1) {
            return { row: row + 1, col: index + 1 };
          }
        }
        return null;
      }
      
      function encode(message, key) {
        const square = buildPolybiusSquare(key);
        let ciphertext = "";
      
        for (let i = 0; i < message.length; i += 2) {
          const letter1 = message[i].toUpperCase(); // Convert to uppercase
          const letter2 = message[i + 1] || 'X'; // Use 'X' for padding
      
          const index1 = findLetterIndex(letter1, square);
          const index2 = findLetterIndex(letter2, square);
      
          if (index1 && index2) {
            ciphertext += `${index1.row}${index2.row}${index1.col}${index2.col}`;
          } else {
            // Handle undefined indices
            console.warn(`Encountered undefined index for characters: ${letter1}, ${letter2}`);
          }
        }
      
        return ciphertext;
      }
      
      
      
    
    // Function to decrypt ciphertext using the Bifid Cipher
    function decode(ciphertext, key) {
        const square = buildPolybiusSquare(key);
        let message = "";
      
        for (let i = 0; i < ciphertext.length; i += 4) {
          const row1 = parseInt(ciphertext[i]);
          const row2 = parseInt(ciphertext[i + 1]);
          const col1 = parseInt(ciphertext[i + 2]);
          const col2 = parseInt(ciphertext[i + 3]);
      
          if (!isNaN(row1) && !isNaN(row2) && !isNaN(col1) && !isNaN(col2)) {
            const letter1 = square[row1 - 1][col1 - 1];
            const letter2 = square[row2 - 1][col2 - 1];
            message += letter1 || ''; // Handle cases where square doesn't have a value
            message += letter2 || '';
          } else {
            // Handle invalid characters in ciphertext (e.g., log a warning or skip)
            console.warn("Encountered invalid character during decryption.");
          }
        }
      
        return message;
      }
      

    const showInformation = () => {
        const info = (
            <>
                <p>
    The Autokey Cipher is a substitution cipher that extends the Vigenère cipher. It utilizes a key that is as long as the plaintext, making it a polyalphabetic substitution cipher.
</p>
<p>
    To encrypt using the Autokey Cipher, each character of the plaintext is combined with a character from the key to produce the ciphertext.
</p>
<ul>
    <li>Substitution Cipher: Each character in the plaintext is replaced by a character derived from a combination of the plaintext and the key.</li>
    <li>Key Length: The key used in the Autokey Cipher is as long as the plaintext, extending the Vigenère cipher's key.</li>
    <li>Encryption: To encrypt, each character in the plaintext is combined with a character from the key using a mathematical operation, typically modular addition.</li>
    <li>Decryption: Decryption involves reversing the encryption process, subtracting the key character from the ciphertext character to retrieve the plaintext character.</li>
    <li>Example: Using a key 'KEY', if 'H' in the plaintext is combined with 'K' from the key, the resulting ciphertext character might be 'S'.</li>
</ul>
            </>
        );
        ongetInfo(info);
    };

    React.useEffect(() => {
        showInformation();
    }, []);

    return <CipherFactory encode={encode} decode={decode} keyComponentA={'STR'}/>
};
