import React from 'react';
import CipherFactory from '../../ui/EncryptDecrypt';


export default function ADFGVX({ongetInfo}) {

function encode(text,keyword,transpositionKey) {
   // Generate the ADFGVX grid based on the keyword
   const sortedKeyword = [...new Set(keyword.split('').sort())];
   const grid = [];
   for (let char of sortedKeyword) {
     let row = [];
     for (let i = 0; i < 6; i++) {
       let value = String.fromCharCode(65 + i);
       row.push(char + value);
     }
     grid.push(row);
   }
 
   // Encrypt text using ADFGVX cipher
   let ciphertext = '';
   for (let char of text) {
     for (let row = 0; row < grid.length; row++) {
       for (let col = 0; col < grid[row].length; col++) {
         if (grid[row][col].includes(char.toUpperCase())) {
           ciphertext += 'ADFGVX'[row] + 'ADFGVX'[col];
         }
       }
     }
   }
   
   // Perform transposition if transpositionKey is provided
   if (transpositionKey) {
     // Convert the ciphertext to a matrix based on the length of transpositionKey
     const matrix = [];
     for (let i = 0; i < ciphertext.length; i += transpositionKey.length) {
       matrix.push(ciphertext.slice(i, i + transpositionKey.length).split(''));
     }
     
     // Apply transposition based on transpositionKey
     const transposedMatrix = [];
     for (let i = 0; i < transpositionKey.length; i++) {
       const index = transpositionKey.indexOf(String.fromCharCode(65 + i));
       for (let j = 0; j < matrix.length; j++) {
         if (!transposedMatrix[j]) {
           transposedMatrix[j] = [];
         }
         transposedMatrix[j].push(matrix[j][index]);
       }
     }
     
     // Convert transposed matrix back to ciphertext
     ciphertext = transposedMatrix.map(row => row.join('')).join('');
   }
 
   return ciphertext;
  }
  
  // Decode a string using ASCII cipher
  function decode(str) {
    var result = '';
    var elements = str.split('-');
    for (var i = 0; i < elements.length; i++) {
      result += String.fromCharCode(elements[i]);
    }
    return result;
  }      
  const showInformation = () => {
    const info = (
        <>
            <p>
                The ASCII Cipher is a simple character substitution cipher based on the ASCII encoding standard. 
                Each character in the plaintext is replaced with its corresponding ASCII value.
            </p>
            <p>
                To encrypt using the ASCII Cipher, simply convert each character of your text to its ASCII value.
            </p>
            <ul>
                <li>Substitution Cipher: Each character in the plaintext is replaced by its corresponding ASCII value.</li>
                <li>ASCII Encoding: ASCII (American Standard Code for Information Interchange) is a character encoding standard that represents text in computers and other devices.</li>
                <li>Encryption: To encrypt, convert each character in the plaintext to its ASCII value.</li>
                <li>Decryption: Decryption involves converting each ASCII value back to its corresponding character.</li>
                <li>Example: 'A' becomes '65', 'B' becomes '66', 'C' becomes '67', and so forth.</li>
            </ul>
        </>
    );
    ongetInfo(info);
};

React.useEffect(() => {
    showInformation();
}, []);

      return <CipherFactory encode={encode} decode={decode} keyComponentA={'STR'} keyComponentB={'STR'} />
};
