import React,{useState} from "react";
import { Layout, Menu, Typography, Watermark, Spin, Modal } from 'antd';

import lodash from 'lodash'
import AtbashCipher from "./components/ciphers/atbash"
import CaesarCipher from "./components/ciphers/caesar";
import BinaryEncoding from "./components/ciphers/binary";

const { Content, Header } = Layout;
const { Title } = Typography;

const App = () => {
  const [showInfo, setShowInfo] = useState(false);
  const [cipherInfo, setCipherInfo] = useState('');

  const toggleInfo=()=>{
    setShowInfo(!showInfo)
  }

  const items = [
    ['Caesar Cipher', <CaesarCipher />],
    ['Atbash Cipher', <AtbashCipher ongetInfo={setCipherInfo} />],
    ['Binary Conversion', <BinaryEncoding />],
  ]

  const [current, setCurrent] = React.useState('');
  const [comp, setComp] = React.useState(<CaesarCipher />)
  const [title, setTitle] = React.useState('');

  const onClick = (e) => {
    setCurrent(e.key);
    let k = Number(e.key);
    setComp(items[k][1]); setTitle(items[k][0]);
  };

  return (
    <>
   <Layout >
    <Header style={{textAlign: 'center',color: '#fff', fontSize: 16}}> EN | CRYPTO | DE  <Spin /> PlayGround </Header>
    <Menu onClick={onClick} selectedKeys={[current]} mode="horizontal" items={items.map((ele, index) => ({ label: ele[0], key: index }))} />
    <Watermark content="ICY Labs">
      <Content style={{ padding: '50px' }}>
        <Title style={{ textAlign: 'center' }} underline level={1} type={lodash.sample(['danger', 'success', 'warning'])} >{title}</Title>
        <button className="info-button" onClick={() => { toggleInfo(); setCipherInfo(''); }}>â„¹</button>
        <Modal
                            title={items[current][0]}
                            visible={showInfo}
                            onCancel={toggleInfo}
                            footer={null}
                        >
                            {/* Display the information for the current cipher */}
                            <p>{cipherInfo}</p>
                        </Modal>
        <div>
          {comp}
        </div>
      </Content>

    </Watermark>
  </Layout>

    </>

  );
};

export default App;
