import React from 'react';
import CipherFactory from '../../ui/EncryptDecrypt';

export default function CaesarCipher(props,{ongetInfo}) {

    // Create a function to encode a string using the Caesar cipher
    function encode(str, shift) {
        // Create an array of the characters in the string
        const chars = str.split('');

        // Shift each character by the specified number of positions
        const encodedChars = chars.map((char) => {
            const charCode = char.charCodeAt(0);
            let newCharCode = charCode + shift;
            if (charCode >= 65 && charCode <= 90) {
                // Handle uppercase characters
                if (newCharCode < 65) {
                    newCharCode = 90 - (65 - newCharCode - 1);
                } else if (newCharCode > 90) {
                    newCharCode = 65 + (newCharCode - 90 - 1);
                }
            } else if (charCode >= 97 && charCode <= 122) {
                // Handle lowercase characters
                if (newCharCode < 97) {
                    newCharCode = 122 - (97 - newCharCode - 1);
                } else if (newCharCode > 122) {
                    newCharCode = 97 + (newCharCode - 122 - 1);
                }
            }
            return String.fromCharCode(newCharCode);
        });

        // Return the encoded string
        return encodedChars.join('');
    }

    // Create a function to decode a string using the Caesar cipher
    function decode(str, shift) {
        // Encode the string with a negative shift to reverse the original shift
        return encode(str, -shift);
    }

    const showInformation=()=>{
        const info=(
            <div className="info-container">
          <p>
            The Caesar Cipher is a substitution cipher where each letter in the
            plaintext is shifted a certain number of places down or up the
            alphabet.
          </p>
          <p>
            To encrypt using the Caesar Cipher, enter your text and specify the
            shift amount.
          </p>
          <ul>
            <li>
              Substitution Cipher: Each letter in the plaintext is replaced by a
              letter some fixed number of positions down or up the alphabet.
            </li>
            <li>
              Shifting Letters: Each letter in the plaintext is shifted by a
              certain number of places up or down the alphabet, known as the
              "key" or "shift value".
            </li>
            <li>
              Encryption: To encrypt, specify the shift amount, and shift each
              letter in the plaintext by that amount to get the ciphertext
              letter.
            </li>
            <li>
              Decryption: To decrypt, know the shift amount used for encryption,
              and shift each letter in the ciphertext back by that amount to
              retrieve the original plaintext letter.
            </li>
            <li>
              Example: With a shift of 3, 'A' would be replaced by 'D', 'B'
              would become 'E', and so on. Conversely, 'D' would be replaced by
              'A', 'E' would become 'B', and so forth.
            </li>
          </ul>
        </div>
        )
        ongetInfo(info)
    }
    React.useEffect(() => {
        showInformation();
      }, []);
    return <CipherFactory encode={encode} decode={decode} keyComponentA={1} />
};
