import React from 'react';
import CipherFactory from '../../ui/EncryptDecrypt';

export default function BinaryEncoding({ongetInfo}) {
  //const [inputText, setInputText] = React.useState('');
    const [inputChars, setInputChars] = React.useState([]);
    const [outputChars, setOutputChars] = React.useState([]);
    const encoding=[]
    const decoding=[]
    function encode(str) {
      let result =Number(str)
      result= (result >>> 0).toString(2);
      encoding.push(str)
      decoding.push(result.toString())
      setInputChars([...inputChars,str])
      setInputChars([...outputChars,result.toString()])
      return result.toString()
    }
    
    function decode(bin) {
      let result =Number(bin)
      result= (result >>> 0).toString(2);
      encoding.push(bin)
      decoding.push(result.toString())
      setInputChars(...inputChars,bin)
      setInputChars(...outputChars,result.toString())
      return result.toString()
    }
      const showInformation=()=>{
        const info=(
          <>
            <p>
      Decimal to Binary Conversion is the process of converting a decimal number to its binary representation.
    </p>
    <ul>
      <li>Decimal Number: A number expressed in base-10, using digits from 0 to 9.</li>
      <li>Binary Representation: Binary is a base-2 numeral system, using only 0 and 1, where each digit represents a power of 2.</li>
      <li>Conversion Process: To convert a decimal number to binary, repeatedly divide the number by 2 and note the remainders, then read the remainders in reverse order to obtain the binary representation.</li>
      <li>Example: For decimal number 10, dividing by 2 yields remainders 0, 1, 0, 1 (reading in reverse gives binary 1010).</li>
      <li>Uses: Binary representation is commonly used in digital electronics and computing for storing and processing data.</li>
    </ul>

          </>
        )
        ongetInfo(info)
      }

      React.useEffect(()=>{
        showInformation()
      },[])
      return (
      <>
        <div style={{ border: '1px solid black', padding: '10px', margin: '20px',overflow:'scroll' ,height:'200px'}}>
            <table style={{ width: '100%', borderCollapse: 'collapse', fontFamily: 'arial, sans-serif' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid black', padding: '8px', fontSize: '20px' }}>Input Text</th>
                        <th style={{ border: '1px solid black', padding: '8px', fontSize: '20px' }}>Output Text</th>
                    </tr>
                </thead>
                <tbody style={{ textAlign: 'center' }}>
                    {inputChars.length>0 && inputChars.map((char, index) => (
                        <tr key={index}>
                            <td style={{ border: '1px solid black', padding: '8px' }}>{char}</td>
                            <td style={{ border: '1px solid black', padding: '8px' }}>{outputChars[index]}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
        <CipherFactory encode={encode} decode={decode} />
      </>
      )


};
