import { Typography, Flex } from "antd";

const { Title,Image, Text, Link } = Typography;

const Header = () => {
  return <Title>Affine Cipher</Title>;
};

const Description = () => {
  return (
    <div>
      <Text>
      The AutoKey Cipher is a polyalphabetic substitution cipher used for encryption. It enhances the Vigen√®re Cipher by incorporating the plaintext into the key. Initially, a short key is chosen, and then the plaintext itself is appended to this key. This method ensures that each letter is encrypted using a different shift value, making the encryption more secure. It's a more complex yet effective way to obscure text by using the plaintext to influence the key.
      </Text>
    </div>
  );
};

const Example = () => {
  return (
    <Flex vertical={true}>
<Text>Write down the plaintext message: HELLO.</Text>
<Text>Choose a key. For this example, we will use the key: KEY.</Text>
<Text>Use the AutoKey Cipher to encode the message.</Text>
<Text>First, write the key followed by the plaintext message: KEYHELLO.</Text>
<Text>Replace each letter in the plaintext message with the corresponding letter from the key sequence.</Text>
<Text>Shift the alphabet for each letter in the key sequence.</Text>
<Text>The first letter of the key is K, which corresponds to a shift of 10 (K is the 11th letter).</Text>
<Text>H shifted by 10 positions becomes R (H is the 8th letter, R is the 18th letter).</Text>
<Text>The next letter of the key is E, which corresponds to a shift of 4 (E is the 5th letter).</Text>
<Text>E shifted by 4 positions becomes I (E is the 5th letter, I is the 9th letter).</Text>
<Text>The next letter of the key is Y, which corresponds to a shift of 24 (Y is the 25th letter).</Text>
<Text>L shifted by 24 positions becomes J (L is the 12th letter, J is the 10th letter when wrapping around the alphabet).</Text>
<Text>The next letter of the plaintext is H, which corresponds to a shift of 7 (H is the 8th letter).</Text>
<Text>L shifted by 7 positions becomes S (L is the 12th letter, S is the 19th letter).</Text>
<Text>The next letter of the plaintext is E, which corresponds to a shift of 4 (E is the 5th letter).</Text>
<Text>O shifted by 4 positions becomes S (O is the 15th letter, S is the 19th letter).</Text>
<Text>So, the encoded message is: RISSS.</Text>

      <Flex wrap="wrap" justify="center" gap={10} style={{ margin: 20 }}>
        <Image src="https://media.geeksforgeeks.org/wp-content/uploads/20200507130007/Capture468.png" />
     
      </Flex>
    </Flex>
  );
};

const References = () => {
  return (
    <Flex vertical={true}>
      <Link
        href="https://www.geeksforgeeks.org/autokey-cipher-symmetric-ciphers/"
        target="_blank"
      >
        https://www.geeksforgeeks.org/autokey-cipher-symmetric-ciphers/       
        </Link>
      
    </Flex>
  );
};

export { Header, Description, Example, References };
